function limpaDadosFormularioUsuario() {
	
	$("#idUsuario").val("0");
	$('#senhaAtualUsuario').val('');
	$('#senhaNovaUsuario').val('');
	$('#senhaConfirmaUsuario').val('');
	
	$("#senhaAtualUsuario").focus();
	
}
