function nomeTabsTracafe(tipo) {
	
	switch (tipo) {
		case 1: 
			return { 
				tabTracafe1: "Dados",
				tabTracafe2: "Despejo",
				tabTracafe3: "Observações"
			};
			
			break;
		case 2: 
			return { 
				tabTracafe1: "Dados",
				tabTracafe2: "Despejo",
				tabTracafe3: "Lotes"
			};

			break;
	}
	
}
